import { useState, useEffect } from "react";
import { getAuth, updateProfile } from "firebase/auth";
import { db } from "../firebase";
import { doc, updateDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";

const EditProfile = () => {
  const auth = getAuth();
  const [userInfo, setUserInfo] = useState({
    name: "",
    email: "",
    photoURL: "",
  });
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [uploading, setUploading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (auth.currentUser) {
      setUserInfo({
        name: auth.currentUser.displayName || "",
        email: auth.currentUser.email || "",
        photoURL: auth.currentUser.photoURL || "",
      });
    } else {
      navigate("/"); // หากผู้ใช้ไม่ได้ล็อกอินให้ไปที่หน้า login
    }
  }, [auth, navigate]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    setUploading(true); // เริ่มการอัปโหลด
    try {
      let imageUrl = userInfo.photoURL;

      if (imageFile) {
        // สร้าง Promise เพื่อรอการอ่านไฟล์
        imageUrl = await new Promise((resolve, reject) => {
          const reader = new FileReader();
          reader.onloadend = () => {
            resolve(reader.result); // resolve Promise ด้วย Data URL
          };
          reader.onerror = reject; // reject ถ้าเกิด error
          reader.readAsDataURL(imageFile);
        });
      }


      // อัปเดตโปรไฟล์ใน Firebase Authentication
      await updateProfile(auth.currentUser, {
        displayName: userInfo.name, // เพิ่ม displayName เข้าไปด้วย
        photoURL: imageUrl,
      });

      // อัปเดตข้อมูลใน Firestore
      const userRef = doc(db, "users", auth.currentUser.uid);
      await updateDoc(userRef, {
        name: userInfo.name,
        email: userInfo.email,
        photo: imageUrl,
      });

      alert("อัปเดตโปรไฟล์สำเร็จ!");
      navigate("/dashboard");
    } catch (error) {
      console.error("Error updating profile: ", error);
      alert("เกิดข้อผิดพลาดในการอัปเดตโปรไฟล์!");
    } finally {
      setUploading(false); // สิ้นสุดการอัปโหลด (ไม่ว่าจะสำเร็จหรือไม่)
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-3">แก้ไขข้อมูลส่วนตัว</h1>
      <form onSubmit={handleUpdateProfile}>
        {/* แสดงรูปโปรไฟล์ปัจจุบัน */}
        <div className="mb-3 text-center">
          {imagePreview ? (
            <img
              src={imagePreview}
              alt="Profile Preview"
              className="img-thumbnail w-25 mb-2"
            />
          ) : (
            <img
              src={userInfo.photoURL || "default-profile-pic.jpg"}
              alt="Current Profile"
              className="img-thumbnail w-25 mb-2"
            />
          )}
        </div>

        {/* ปุ่มเลือกไฟล์ */}
        <div className="mb-3">
          <label className="form-label">อัปโหลดรูปภาพใหม่</label>
          <input type="file" className="form-control" onChange={handleImageChange} />
        </div>

        {/* ฟอร์มแก้ไขชื่อ */}
        <div className="mb-3">
          <label className="form-label">ชื่อ</label>
          <input
            type="text"
            className="form-control"
            value={userInfo.name}
            onChange={(e) => setUserInfo({ ...userInfo, name: e.target.value })}
          />
        </div>

        {/* ฟอร์มแสดงอีเมล (แก้ไขไม่ได้) */}
        <div className="mb-3">
          <label className="form-label">อีเมล</label>
          <input type="email" className="form-control" value={userInfo.email} disabled />
        </div>

        {/* ปุ่มบันทึก */}
        <button type="submit" className="btn btn-success" disabled={uploading}>
          {uploading ? "กำลังอัปโหลด..." : "อัปเดตข้อมูล"}
        </button>
      </form>
    </div>
  );
};

export default EditProfile;
