import { useState, useEffect, useCallback } from "react";
import { db } from "../firebase";
import { getAuth, signOut } from "firebase/auth";
import { collection, query, where, getDocs, doc, getDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const Dashboard = () => {
  const [classes, setClasses] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const fetchClasses = useCallback(async () => {
    const auth = getAuth();
    if (!auth.currentUser) return;
    const q = query(collection(db, "classroom"), where("owner", "==", auth.currentUser.uid));
    const querySnapshot = await getDocs(q);
    setClasses(querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      const auth = getAuth();
      if (!auth.currentUser) {
        navigate("/");
        return;
      }
      const userDocRef = doc(db, "users", auth.currentUser.uid);
      const docSnap = await getDoc(userDocRef);
      if (docSnap.exists()) {
        setUser(docSnap.data());
      } else {
        console.error("User not found in Firestore");
        navigate("/");
      }
    };
    fetchData();
    fetchClasses();
  }, [fetchClasses, navigate]);

  const handleLogout = async () => {
    const auth = getAuth();
    try {
      await signOut(auth);
      navigate("/");
    } catch (error) {
      console.error("Logout failed:", error);
    }
  };

  return (
    <div className="container-fluid bg-white min-vh-100 py-4">
      <div className="container p-3">
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h1 className="h3 fw-normal">Dashboard</h1>
          <button className="btn btn-sm btn-light" onClick={handleLogout}>ออกจากระบบ</button>
        </div>

        {user && (
          <div className="mb-4 p-3 border-bottom">
            <div className="d-flex align-items-center">
              <img 
                src={user.photo || "default-profile.jpg"} 
                alt="User Profile" 
                className="rounded-circle me-3" 
                width="50" 
                height="50" 
              />
              <div>
                <h4 className="h5 mb-1">{user.name}</h4>
                <p className="text-muted small mb-0">{user.email}</p>
              </div>
            </div>
          </div>
        )}

        <div className="row mb-4 g-2">
          <div className="col-6">
            <button className="btn btn-sm btn-light w-100" onClick={() => navigate("/edit-profile")}>
              แก้ไขโปรไฟล์
            </button>
          </div>
          <div className="col-6">
            <button className="btn btn-sm btn-light w-100" onClick={() => navigate("/add-class")}>
              เพิ่มห้องเรียน
            </button>
          </div>
        </div>

        <h2 className="h5 mb-3">ห้องเรียนของคุณ</h2>
        
        <div className="row g-3">
          {classes.length > 0 ? (
            classes.map((cls) => (
              <div key={cls.id} className="col-md-6 col-lg-4">
                <div className="card border-0 shadow-sm">
                  {cls.info.photo && (
                    <img 
                      src={cls.info.photo} 
                      className="card-img-top" 
                      alt="Class"
                      style={{ height: "120px", objectFit: "cover" }}
                    />
                  )}
                  <div className="card-body">
                    <h5 className="card-title h6">{cls.info.code} - {cls.info.name}</h5>
                    <p className="text-muted small">ห้อง: {cls.info.room}</p>
                    <button 
                      className="btn btn-sm btn-light w-100" 
                      onClick={() => navigate(`/manage-class/${cls.id}`)}
                    >
                      จัดการห้องเรียน
                    </button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="col-12">
              <p className="text-muted">ยังไม่มีห้องเรียน</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
