import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { db } from "../firebase";
import { doc, getDoc, collection, query, getDocs } from "firebase/firestore";
import { QRCodeCanvas } from "qrcode.react";
import "bootstrap/dist/css/bootstrap.min.css";

const ManageClass = () => {
  const { classId } = useParams();
  const navigate = useNavigate();
  const [classInfo, setClassInfo] = useState(null);
  const [checkins, setCheckins] = useState([]);
  const [students, setStudents] = useState([]);
  const [showStudents, setShowStudents] = useState(false); // แสดง/ซ่อนรายชื่อนักศึกษา
  const [checkinStudents, setCheckinStudents] = useState(null); // รายชื่อนักศึกษาที่เช็คชื่อในรอบนั้น
  //const [answers, setAnswers] = useState({}); // เก็บคำตอบของนักศึกษา  <-- ไม่จำเป็นต้องใช้ state นี้แล้ว เพราะดึง answers มาพร้อม checkin data

  useEffect(() => {
    const fetchClassData = async () => {
      if (!classId) return;
      const classRef = doc(db, "classroom", classId);
      const classSnap = await getDoc(classRef);
      if (classSnap.exists()) {
        setClassInfo(classSnap.data());
      } else {
        console.error("Class not found");
        navigate("/dashboard");
      }
    };

    const fetchCheckins = async () => {
      const checkinQuery = query(collection(db, `classroom/${classId}/checkin`));
      const checkinSnapshot = await getDocs(checkinQuery);

      const checkinData = await Promise.all(
        checkinSnapshot.docs.map(async (docSnap) => {
          const checkinInfo = docSnap.data();
          // ไม่ต้อง query students ตรงนี้ เพราะเราจะ query answers แทน
          // const studentQuery = collection(db, `classroom/${classId}/checkin/${docSnap.id}/students`);
          // const studentSnapshot = await getDocs(studentQuery);

          // ดึง answers *ทั้งหมด* ของ check-in ครั้งนี้
          const answersData = await fetchAnswers(docSnap.id);

          // *สำคัญ*:  Query หา students ที่อยู่ใน checkin *นี้*
          const studentsQuery = query(collection(db, `classroom/${classId}/checkin/${docSnap.id}/students`));
          const studentsSnapshot = await getDocs(studentsQuery);
          const studentsInThisCheckin = studentsSnapshot.docs.map(studentDoc => studentDoc.data());

          return {
            id: docSnap.id,
            ...checkinInfo,
            // studentCount: studentSnapshot.size,  // ใช้ studentsInThisCheckin.length แทนได้
            studentCount: studentsInThisCheckin.length,  // จำนวนคนที่อยู่ใน check-in นี้
            // students: studentSnapshot.docs.map((studentDoc) => studentDoc.data()), // ไม่จำเป็น
            students: studentsInThisCheckin, // เก็บรายชื่อนักศึกษา *ที่อยู่ใน check-in นี้*
            answers: answersData, // เพิ่มคำตอบในข้อมูล check-in
          };
        })
      );

      setCheckins(checkinData);
    };

      const fetchAnswers = async (checkinId) => {
          const answersCollection = collection(db, `classroom/${classId}/checkin/${checkinId}/answers`);
          const answersSnapshot = await getDocs(answersCollection);

          const answersData = {};
          for (const answerDoc of answersSnapshot.docs) {
            const answerData = answerDoc.data();  // { text: "...", time: "..." }
             // วนลูปผ่านแต่ละ field ที่เป็น student id
              if (answerData.students) {
                for (const studentId in answerData.students) {
                    if(answerData.students.hasOwnProperty(studentId)) {
                        const studentAnswer = answerData.students[studentId];

                        // สร้างโครงสร้างข้อมูล answers ให้เหมาะสมกับการใช้งาน
                      if (!answersData[studentId]) {
                        answersData[studentId] = {};
                      }
                      answersData[studentId][answerDoc.id] = studentAnswer;  // เก็บคำตอบของนักศึกษาแต่ละคนในแต่ละคำถาม
                    }
                }
              }
          }
          return answersData;
      };


    const fetchStudents = async () => {
      const studentQuery = query(collection(db, `classroom/${classId}/students`));
      const studentSnapshot = await getDocs(studentQuery);

      const studentList = studentSnapshot.docs.map((docSnap) => ({
        id: docSnap.id,
        ...docSnap.data(),
      }));

      setStudents(studentList);
    };

    fetchClassData();
    fetchCheckins();
    fetchStudents();
  }, [classId, navigate]);

  const handleStartCheckIn = () => {
    navigate(`/checkin/${classId}`);
  };

    const handleViewCheckinStudents = (checkinId) => {
    //   const checkinData = checkins.find((checkin) => checkin.id === checkinId);
    //   setCheckinStudents(checkinData ? checkinData.students : []);

        // Find the selected check-in
        const selectedCheckin = checkins.find(checkin => checkin.id === checkinId);

        if (selectedCheckin) {

              const studentsWithAnswers = selectedCheckin.students.map(student => {

                let studentAnswers = {};
                  if (selectedCheckin.answers && selectedCheckin.answers[student.stdid]) {
                      studentAnswers = selectedCheckin.answers[student.stdid];
                  }
                return {
                  ...student,
                  answers: studentAnswers  // เพิ่ม answers เข้าไปในข้อมูลของนักศึกษาแต่ละคน
                };
              });
             setCheckinStudents(studentsWithAnswers);

        } else {
            setCheckinStudents([]);  // หรือ null ถ้านั่นคือสิ่งที่คุณต้องการ
        }

    };

  // ฟังก์ชันช่วยค้นหานักศึกษาจาก studentId
  const findStudentById = (studentId) => {
    return students.find((student) => student.id === studentId);
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4 text-center">จัดการห้องเรียน</h1>
      {classInfo && (
        <div className="card mb-4">
          <div className="card-body text-center">
            <h4>{classInfo.info.name} ({classInfo.info.code})</h4>
            <QRCodeCanvas value={`${window.location.origin}/register/${classId}`} size={180} />
          </div>
        </div>
      )}

      <button className="btn btn-primary mb-3" onClick={handleStartCheckIn}>เริ่มเช็คชื่อ</button>

      <h5>ประวัติการเช็คชื่อ</h5>
      <table className="table">
        <thead>
          <tr>
            <th>#</th>
            <th>วัน-เวลา</th>
            <th>จำนวนคนเข้าเรียน</th>
            <th>สถานะ</th>
            <th>ดูรายชื่อนักศึกษา</th>
          </tr>
        </thead>
        <tbody>
          {checkins.map((record, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{record.date}</td>
              <td>{record.studentCount}</td>
              <td>{record.status === 1 ? "กำลังเรียน" : "เสร็จสิ้น"}</td>
              <td>
                <button className="btn btn-info" onClick={() => handleViewCheckinStudents(record.id)}>
                  ดูรายชื่อนักศึกษา
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {checkinStudents && (
        <div className="mt-4">
          <h5>รายชื่อนักศึกษาที่เช็คชื่อในรอบนี้</h5>
          <table className="table">
            <thead>
              <tr>
                <th>#</th>
                <th>รหัส</th>
                <th>ชื่อ</th>
                <th>คำตอบ</th> {/* เพิ่มคอลัมน์คำตอบ */}
              </tr>
            </thead>
            <tbody>
              {checkinStudents.map((student, index) => {
                const studentInfo = findStudentById(student.stdid); // ค้นหานักศึกษาจาก ID

                // ไม่จำเป็นต้องใช้ checkins.find อีกแล้ว เพราะ answers อยู่ใน checkinStudents โดยตรง
                // const checkinData = checkins.find(checkin => checkin.id === student.checkinId);

                return studentInfo ? (
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td>{studentInfo.stdid}</td>
                    <td>{studentInfo.name}</td>
                    <td>
                        {/* แสดงคำตอบจากนักเรียน */}
                        {/* {checkinData && checkinData.answers && checkinData.answers[student.stdid]
                          ? checkinData.answers[student.stdid].text
                          : "ยังไม่มีคำตอบ"} */}

                        {/* แสดงคำตอบ (ถ้ามี) */}
                        {
                            Object.keys(student.answers).map(questionId => (
                                <div key={questionId}>
                                    <strong>คำถาม {questionId}: </strong>
                                    {student.answers[questionId].text || "ไม่มีคำตอบ"}
                                </div>
                            ))
                        }
                    </td>
                  </tr>
                ) : null;
              })}
            </tbody>
          </table>
        </div>
      )}


      <button className="btn btn-secondary mt-3" onClick={() => navigate("/dashboard")}>กลับไป Dashboard</button>

      {/* ปุ่มเปิด/ปิดแสดงรายชื่อนักศึกษา */}
      <button className="btn btn-info mt-3 ms-2" onClick={() => setShowStudents(!showStudents)}>
        {showStudents ? "ซ่อนรายชื่อนักศึกษา" : "แสดงรายชื่อนักศึกษา"}
      </button>

      {/* แสดงรายชื่อนักศึกษาเมื่อ showStudents เป็น true */}
      {showStudents && (
        <div className="mt-4">
          <h5>รายชื่อนักศึกษา</h5>
          <table className="table">
            <thead>
              <tr>
                <th>#</th>
                <th>รหัส</th>
                <th>ชื่อ</th>
                <th>รูปภาพ</th>
              </tr>
            </thead>
            <tbody>
              {students.map((student, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{student.stdid}</td>
                  <td>{student.name}</td>
                  <td>
                    <img src={student.photo} alt="Student" width="50" height="50" />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ManageClass;
