import { auth, provider, db } from "../firebase";
import { signInWithPopup } from "firebase/auth";
import { doc, setDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap-icons/font/bootstrap-icons.css";

const Login = () => {
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      await setDoc(doc(db, "users", user.uid), {
        name: user.displayName,
        email: user.email,
        photo: user.photoURL,
      }, { merge: true });

      navigate("/dashboard");
    } catch (error) {
      console.error("Login Failed", error);
      alert("Login failed! Please try again.");
    }
  };

  return (
    <div
      className="container d-flex justify-content-center align-items-center vh-100"
      style={styles.container} // ใช้ inline styles
    >
      <div className="card p-4 shadow-lg text-center" style={styles.card}>
        <h1 className="mb-4">เข้าสู่ระบบ</h1>
        <button
          onClick={handleLogin}
          className="btn btn-primary btn-lg"
          style={styles.button}
        >
          <i className="bi bi-google me-2"></i>
          Login with Google
        </button>
        <p className="mt-3 text-muted">
          Sign in to access your dashboard and manage your account.
        </p>
      </div>
    </div>
  );
};

// ✅ เพิ่ม: object styles สำหรับ inline styles
const styles = {
  container: {
    backgroundColor: "#f8f9fa", // สีพื้นหลังอ่อนๆ
    // backgroundImage: "url('path/to/your/background.jpg')", // ถ้าต้องการใช้รูปภาพ
    // backgroundSize: "cover",
    // backgroundPosition: "center",
  },
  card: {
    maxWidth: "400px",
    width: "90%",
    borderRadius: "10px", // ขอบมน
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)", // เงา
  },
  button: {
    backgroundColor: "#db4437", // สี Google Red
    borderColor: "#db4437",
    color: "white",
  },
  buttonHover: {  // ไม่สามารถใช้ :hover กับ inline styles ตรงๆ ได้ ต้องใช้ JavaScript
    backgroundColor: "#c82333", // สีเข้มขึ้นเมื่อ hover
    borderColor: "#bd2130",
  }
};

export default Login;
