import { useState, useEffect, useCallback } from "react";
import { db } from "../firebase";
import { getAuth, signOut } from "firebase/auth";
import { collection, query, where, getDocs, doc, getDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const Dashboard = () => {
  const [classes, setClasses] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const fetchClasses = useCallback(async () => {
    const auth = getAuth();
    if (!auth.currentUser) return;
    const q = query(collection(db, "classroom"), where("owner", "==", auth.currentUser.uid));
    const querySnapshot = await getDocs(q);
    setClasses(querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      const auth = getAuth();
      if (!auth.currentUser) {
        navigate("/");
        return;
      }
      const userDocRef = doc(db, "users", auth.currentUser.uid);
      const docSnap = await getDoc(userDocRef);
      if (docSnap.exists()) {
        setUser(docSnap.data());
      } else {
        console.error("User not found in Firestore");
        navigate("/");
      }
    };
    fetchData();
    fetchClasses();
  }, [fetchClasses, navigate]);

  const handleLogout = async () => {
    const auth = getAuth();
    try {
      await signOut(auth);
      navigate("/");
    } catch (error) {
      console.error("Logout failed:", error);
    }
  };

  return (
    <div className="container-fluid bg-light min-vh-100 py-5">
      <div className="container bg-white p-4 rounded shadow">
        {/* Header Section */}
        <div className="row border-bottom pb-3 mb-4">
          <div className="col-md-8">
            <h2 className="fw-bold text-dark">ระบบจัดการห้องเรียน</h2>
          </div>
          <div className="col-md-4 text-md-end">
            <button className="btn btn-outline-secondary" onClick={handleLogout}>
              <i className="bi bi-box-arrow-right me-2"></i>ออกจากระบบ
            </button>
          </div>
        </div>

        {/* User Profile Section */}
        {user && (
          <div className="row mb-4">
            <div className="col-md-12">
              <div className="card border-0 bg-light">
                <div className="card-body d-flex align-items-center">
                  <img 
                    src={user.photo || "default-profile.jpg"} 
                    alt="User Profile" 
                    className="rounded-circle me-3 border" 
                    width="60" 
                    height="60" 
                  />
                  <div>
                    <h5 className="fw-semibold mb-1">{user.name}</h5>
                    <p className="text-muted mb-0">{user.email}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Action Buttons */}
        <div className="row mb-4">
          <div className="col-md-6 mb-2 mb-md-0">
            <button 
              className="btn btn-light border w-100 py-2" 
              onClick={() => navigate("/edit-profile")}
            >
              แก้ไขโปรไฟล์
            </button>
          </div>
          <div className="col-md-6">
            <button 
              className="btn btn-primary w-100 py-2" 
              onClick={() => navigate("/add-class")}
            >
              เพิ่มห้องเรียน
            </button>
          </div>
        </div>

        {/* Class Section Header */}
        <div className="row border-bottom pb-2 mb-3">
          <div className="col">
            <h4 className="fw-semibold text-dark">รายการห้องเรียน</h4>
          </div>
        </div>

        {/* Classes List */}
        <div className="row">
          {classes.length > 0 ? (
            classes.map((cls) => (
              <div key={cls.id} className="col-md-6 col-lg-4 mb-4">
                <div className="card h-100 border">
                  {cls.info.photo && (
                    <div className="card-img-container" style={{ height: "140px", overflow: "hidden" }}>
                      <img 
                        src={cls.info.photo} 
                        className="card-img-top" 
                        alt="Class"
                        style={{ objectFit: "cover", height: "100%", width: "100%" }}
                      />
                    </div>
                  )}
                  <div className="card-body">
                    <h5 className="card-title fw-semibold">{cls.info.code}</h5>
                    <h6 className="card-subtitle mb-2 text-muted">{cls.info.name}</h6>
                    <p className="card-text small text-muted mb-3">
                      <i className="bi bi-geo-alt me-1"></i>ห้อง: {cls.info.room}
                    </p>
                    <button 
                      className="btn btn-sm btn-outline-dark w-100" 
                      onClick={() => navigate(`/manage-class/${cls.id}`)}
                    >
                      จัดการห้องเรียน
                    </button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="col-12 text-center py-4">
              <p className="text-muted">ไม่พบรายการห้องเรียน</p>
              <button 
                className="btn btn-primary" 
                onClick={() => navigate("/add-class")}
              >
                เพิ่มห้องเรียนใหม่
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
