import { auth, provider, db } from "../firebase";
import { signInWithPopup } from "firebase/auth";
import { doc, setDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css"; // ✅ นำเข้า Bootstrap
import { FcGoogle } from "react-icons/fc"; // เพิ่ม icon google


const Login = () => {
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      await setDoc(doc(db, "users", user.uid), {
        name: user.displayName,
        email: user.email,
        photo: user.photoURL,
      }, { merge: true });

      navigate("/dashboard");
    } catch (error) {
      console.error("Login Failed", error);
    }
  };

  return (
    <div
      className="container d-flex justify-content-center align-items-center vh-100"
      style={{
        backgroundColor: "#f8f0e5", // สีพื้นหลังพาสเทล
      }}
    >
      <div
        className="card p-4 shadow-lg text-center"
        style={{
          backgroundColor: "#fff", // สีพื้นหลัง card เป็นสีขาว
          borderRadius: "15px",   // ขอบมน
          border: "none",        // ไม่มีเส้นขอบ
          maxWidth: "400px",      // จำกัดความกว้างสูงสุด
          width: "90%"          // ความกว้าง 90% ของ container
        }}
      >
        <h1
          className="mb-4"
          style={{
            color: "#333",     // สีตัวอักษรเข้ม (ดำ)
            fontFamily: "'Helvetica Neue', sans-serif", // font ที่ดู minimal
            fontWeight: "500",   // ความหนาของ font (medium)
          }}
        >
          เข้าสู่ระบบ
        </h1>
        <button
          onClick={handleLogin}
          className="btn btn-light border" // ใช้ btn-light และ border เพื่อให้ดู minimal ขึ้น
          style={{
            color: "#333",
            padding: "10px 20px", // เพิ่ม padding
            borderRadius: "8px",  // ขอบมน
            display: "flex",       // ทำให้ icon กับ text อยู่ใน line เดียวกัน
            alignItems: "center",  // จัดให้อยู่ตรงกลางในแนวตั้ง
            justifyContent: "center",// จัดให้อยู่ตรงกลางในแนวนอน
            gap: "10px",         // ระยะห่างระหว่าง icon กับ text
            width: "100%"         // ทำให้ button เต็มความกว้างของ card
          }}
        >
           <FcGoogle size={24} /> Login with Google
        </button>
      </div>
    </div>
  );
};

export default Login;
