import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { db } from "../firebase";
import {
  doc,
  getDoc,
  updateDoc,
  collection,
  query,
  getDocs,
  setDoc,
  onSnapshot,
} from "firebase/firestore";
import { QRCodeCanvas } from "qrcode.react";
import { v4 as uuidv4 } from "uuid";
import "bootstrap/dist/css/bootstrap.min.css";

const CheckinPage = () => {
  const { classId } = useParams();
  const navigate = useNavigate();
  const [checkins, setCheckins] = useState([]);
  const [studentsCheckedIn, setStudentsCheckedIn] = useState([]);
  const [latestCheckinNo, setLatestCheckinNo] = useState(null);
  const [studentsScores, setStudentsScores] = useState([]);
  const [showScores, setShowScores] = useState(false);
  const [checkinCode, setCheckinCode] = useState(null);
  const [showCheckinCode, setShowCheckinCode] = useState(false);
  const [studentsMap, setStudentsMap] = useState({});
  const [loading, setLoading] = useState(false); // Loading state

  const handleGoToQuestionPage = () => {
    navigate(`/qa/${classId}`);
  };

  const fetchCheckinCode = async () => {
    if (!latestCheckinNo) {
      return;
    }
    const checkinDoc = await getDoc(
      doc(db, `classroom/<span class="math-inline">\{classId\}/checkin/</span>{latestCheckinNo}`)
    );
    if (checkinDoc.exists()) {
      setCheckinCode(checkinDoc.data().code);
      setShowCheckinCode(true);
    } else {
      setCheckinCode(null);
    }
  };

    useEffect(() => {
        const fetchStudents = async () => {
            const studentsRef = collection(db, `classroom/${classId}/students`);
            const studentsSnapshot = await getDocs(studentsRef);
            const studentsData = {};
            studentsSnapshot.forEach((doc) => {
              studentsData[doc.id] = { ...doc.data(), id: doc.id };
            });
            setStudentsMap(studentsData);
        };

        fetchStudents();
      }, [classId]);

  useEffect(() => {
    if (!latestCheckinNo) return;

    const scoresRef = collection(
      db,
      `classroom/<span class="math-inline">\{classId\}/checkin/</span>{latestCheckinNo}/scores`
    );
    const unsubscribe = onSnapshot(scoresRef, (querySnapshot) => {
      const scores = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));

      setStudentsScores(scores);
    });

    return () => unsubscribe();
  }, [classId, latestCheckinNo]);

  const handleSaveScores = async () => {
    setLoading(true); // Start loading
    try {
      for (let student of studentsScores) {
        const studentRef = doc(
          db,
          `classroom/<span class="math-inline">\{classId\}/checkin/</span>{latestCheckinNo}/scores/${student.id}`
        );
        await updateDoc(studentRef, {
          score: student.score,
          remark: student.remark,
          status: student.status,
        });
      }
      alert("‚úÖ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢!");
    } catch (error) {
      console.error("Error updating scores:", error);
      alert("‚ùå ‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•!");
    } finally {
        setLoading(false); // End loading, whether successful or not
    }
  };

  useEffect(() => {
    const q = query(collection(db, `classroom/${classId}/checkin`));
    const unsubscribe = onSnapshot(q, async (querySnapshot) => {
      const checkinList = await Promise.all(
        querySnapshot.docs.map(async (docSnap) => {
          const checkinInfo = docSnap.data();
          const studentQuery = collection(
            db,
            `classroom/<span class="math-inline">\{classId\}/checkin/</span>{docSnap.id}/students`
          );
          const studentSnapshot = await getDocs(studentQuery);
          return {
            id: docSnap.id,
            ...checkinInfo,
            studentCount: studentSnapshot.size,
          };
        })
      );

      setCheckins(checkinList);
      const activeCheckin = checkinList.find((checkin) => checkin.status === 1);
        if (activeCheckin) {
          setLatestCheckinNo(parseInt(activeCheckin.id));
        } else {
          setLatestCheckinNo(null);
        }
    });

    return () => unsubscribe();
  }, [classId]);

  useEffect(() => {
    if (!latestCheckinNo) return;

    const studentCheckinRef = collection(
      db,
      `classroom/<span class="math-inline">\{classId\}/checkin/</span>{latestCheckinNo}/students`
    );
    const unsubscribe = onSnapshot(studentCheckinRef, (querySnapshot) => {
      const checkedInStudents = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setStudentsCheckedIn(checkedInStudents);
    });

    return () => unsubscribe();
  }, [classId, latestCheckinNo]);

  const handleStartCheckIn = async () => {
    setLoading(true); // Start loading
    try {
      const newCno = checkins.length + 1;
      const studentsRef = collection(db, `classroom/${classId}/students`);
      const studentsSnapshot = await getDocs(studentsRef);

      await setDoc(doc(db, `classroom/<span class="math-inline">\{classId\}/checkin/</span>{newCno}`), {
        code: uuidv4().substring(0, 6).toUpperCase(),
        date: new Date().toLocaleString(),
        status: 1,
      });

      studentsSnapshot.forEach(async (student) => {
        await setDoc(
          doc(db, `classroom/<span class="math-inline">\{classId\}/checkin/</span>{newCno}/scores/${student.id}`),
          {
            stdid: student.data().stdid,
            name: student.data().name,
            remark: "",
            score: 0,
            status: 0,
            date: "",
          }
        );
      });

      alert(`‚úÖ ‡πÄ‡∏£‡∏¥‡πà‡∏°‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠‡∏Ñ‡∏£‡∏±‡πâ‡∏á‡∏ó‡∏µ‡πà ${newCno} ‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à!`);
      setLatestCheckinNo(newCno);
    } catch (error) {
      console.error("Error starting new check-in:", error);
      alert("‚ùå ‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡πÄ‡∏£‡∏¥‡πà‡∏°‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠!");
    } finally {
        setLoading(false); // End loading
    }
  };

  const handleCloseCheckIn = async () => {
    setLoading(true); // Start loading
    try {
      if (!latestCheckinNo) {
        alert("‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Å‡∏≤‡∏£‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠‡∏ó‡∏µ‡πà‡πÄ‡∏õ‡∏¥‡∏î‡∏≠‡∏¢‡∏π‡πà!");
        return;
      }

      await updateDoc(
        doc(db, `classroom/<span class="math-inline">\{classId\}/checkin/</span>{latestCheckinNo}`),
        { status: 2 }
      );

      setCheckinCode(null);
      setShowCheckinCode(false);
      setLatestCheckinNo(null);

      alert("‡∏õ‡∏¥‡∏î‡∏Å‡∏≤‡∏£‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢!");
    } catch (error) {
      console.error("Error closing check-in:", error);
      alert("‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏õ‡∏¥‡∏î‡∏Å‡∏≤‡∏£‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠!");
    } finally {
        setLoading(false); // End loading
    }
  };

  return (
    <div className="container mt-5" style={styles.container}>
      <h1 className="mb-4 text-center" style={styles.heading}>
        üìã ‡∏´‡∏ô‡πâ‡∏≤‡∏ï‡∏£‡∏ß‡∏à‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠
      </h1>

      <div className="d-flex justify-content-center gap-3 mb-4">
        <button
          className="btn btn-success"
          onClick={handleStartCheckIn}
          style={styles.button}
          disabled={loading} // Disable button during loading
        >
          {loading ? "‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£..." : "‚úÖ ‡πÄ‡∏£‡∏¥‡πà‡∏°‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠‡πÉ‡∏´‡∏°‡πà"} {/* Show loading text */}
        </button>
        <button
          className="btn btn-danger"
          onClick={handleCloseCheckIn}
          style={styles.button}
          disabled={loading}
        >
            {loading ? "‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£..." : "‚ùå ‡∏õ‡∏¥‡∏î‡∏Å‡∏≤‡∏£‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠"}
        </button>
        <button
          className="btn btn-warning"
          style={styles.button}
          onClick={() => {
            if (showCheckinCode) {
              setShowCheckinCode(false);
              setCheckinCode(null);
            } else {
              fetchCheckinCode();
            }
          }}
        >
          {showCheckinCode ? "üîí ‡∏ã‡πà‡∏≠‡∏ô‡∏£‡∏´‡∏±‡∏™‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠" : "üîç ‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏´‡∏±‡∏™‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠"}
        </button>
      </div>

      {checkinCode && (
        <div className="alert alert-info text-center" style={styles.alert}>
          <h5>
            üìå ‡∏£‡∏´‡∏±‡∏™‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠: <strong>{checkinCode}</strong>
          </h5>
        </div>
      )}

      {checkinCode && (
        <div className="text-center mb-4">
          <h5>üîó QR Code ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠</h5>
          <QRCodeCanvas
            value={`<span class="math-inline">\{window\.location\.origin\}/checkin/</span>{classId}/${checkinCode}`}
            size={200}
            style={styles.qrcode}
          />
        </div>
      )}

      <div className="card" style={styles.card}>
        <div className="card-body">
          <h5 style={styles.cardTitle}>üë• ‡∏£‡∏≤‡∏¢‡∏ä‡∏∑‡πà‡∏≠‡∏ô‡∏±‡∏Å‡∏®‡∏∂‡∏Å‡∏©‡∏≤‡∏ó‡∏µ‡πà‡πÄ‡∏ä‡πá‡∏Ñ‡∏ä‡∏∑‡πà‡∏≠‡πÅ‡∏•‡πâ‡∏ß</h5>
          {studentsCheckedIn.length > 0 ? (
            <table className="table" style={styles.table}>
              <thead>
                <tr>
                  <th style={styles.tableHeader}>‡∏•‡∏≥‡∏î‡∏±‡∏ö</th>
                  <th style={styles.tableHeader}>‡∏£‡∏´‡∏±‡∏™‡∏ô‡∏±‡∏Å‡∏®‡∏∂‡∏Å‡∏©‡∏≤</th>
                  <th style={styles.tableHeader}>‡∏ä‡∏∑‡πà‡∏≠</th>
                  <th style={styles.tableHeader}>‡∏ß‡∏±‡∏ô-‡πÄ‡∏ß‡∏•‡∏≤</th>
                </tr>
              </thead>
              <tbody>
                {studentsCheckedIn
                  .filter((student) => student.date !== "")
                  .map((student, index) => {
                    const studentInfo = studentsMap[student.id];
                    return (
                      <tr key={student.id}>
                        <td style={styles.tableCell}>{index + 1}</td>
                        <td style={styles.tableCell}>{studentInfo?.stdid}</td>
