        </div>

        {user && (
          <div className="card p-3 mb-4 d-flex flex-row align-items-center">
            <img src={user.photo || "default-profile.jpg"} alt="User Profile" className="rounded-circle me-3" width="60" height="60" />
            <div>
              <h4 className="fw-semibold mb-1">{user.name}</h4>
              <p className="text-muted mb-0">{user.email}</p>
            </div>
          </div>
        )}

        <div className="row mb-4">
          <div className="col">
            <button className="btn btn-outline-primary w-100" onClick={() => navigate("/edit-profile")}>‚úèÔ∏è ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå</button>
          </div>
          <div className="col">
            <button className="btn btn-outline-success w-100" onClick={() => navigate("/add-class")}>‚ûï ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏´‡πâ‡∏≠‡∏á‡πÄ‡∏£‡∏µ‡∏¢‡∏ô</button>
          </div>
        </div>

        <h2 className="fw-semibold mb-3">üìù ‡∏´‡πâ‡∏≠‡∏á‡πÄ‡∏£‡∏µ‡∏¢‡∏ô‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì</h2>
        <div className="row">
          {classes.length > 0 ? (
            classes.map((cls) => (
              <div key={cls.id} className="col-md-6 col-lg-4 mb-4">
                <div className="card shadow-sm">
                  {cls.info.photo && <img src={cls.info.photo} className="card-img-top" alt="Class" />}
                  <div className="card-body">
                    <h5 className="card-title fw-bold">{cls.info.code} - {cls.info.name}</h5>
                    <p className="text-muted">üìç ‡∏´‡πâ‡∏≠‡∏á: {cls.info.room}</p>
                    <button className="btn btn-primary w-100" onClick={() => navigate(`/manage-class/${cls.id}`)}>‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏´‡πâ‡∏≠‡∏á‡πÄ‡∏£‡∏µ‡∏¢‡∏ô</button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p className="text-muted">üìå ‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏´‡πâ‡∏≠‡∏á‡πÄ‡∏£‡∏µ‡∏¢‡∏ô</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
