import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { db } from "../firebase";
import { doc, getDoc, collection, query, getDocs } from "firebase/firestore";
import { QRCodeCanvas } from "qrcode.react";
import "bootstrap/dist/css/bootstrap.min.css";

const ManageClass = () => {
  const { classId } = useParams();
  const navigate = useNavigate();
  const [classInfo, setClassInfo] = useState(null);
  const [checkins, setCheckins] = useState([]);
  const [students, setStudents] = useState([]);
  const [showStudents, setShowStudents] = useState(false);
  const [checkinStudents, setCheckinStudents] = useState(null);
  const [studentsMap, setStudentsMap] = useState({});

  useEffect(() => {
    const fetchClassData = async () => {
      if (!classId) return;
      const classRef = doc(db, "classroom", classId);
      const classSnap = await getDoc(classRef);
      if (classSnap.exists()) {
        setClassInfo(classSnap.data());
      } else {
        console.error("Class not found");
        navigate("/dashboard");
      }
    };

    const fetchCheckins = async () => {
      const checkinQuery = query(collection(db, `classroom/${classId}/checkin`));
      const checkinSnapshot = await getDocs(checkinQuery);

      const checkinData = checkinSnapshot.docs.map((docSnap) => ({
        id: docSnap.id,
        ...docSnap.data(),
      }));

      setCheckins(checkinData);
    };

    const fetchStudents = async () => {
      const studentQuery = query(collection(db, `classroom/${classId}/students`));
      const studentSnapshot = await getDocs(studentQuery);

      const studentMapData = {};
      studentSnapshot.docs.forEach((docSnap) => {
        studentMapData[docSnap.id] = {
          id: docSnap.id,
          ...docSnap.data(),
        };
      });
      setStudentsMap(studentMapData);
      setStudents(Object.values(studentMapData));
    };

    fetchClassData();
    fetchCheckins();
    fetchStudents();
  }, [classId, navigate]);

  return (
    <div
      style={{
        background: "#f6f4fd",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        padding: "30px",
      }}
    >
      <div
        style={{
          maxWidth: "1100px",
          width: "100%",
          background: "#ffffff",
          padding: "40px",
          borderRadius: "15px",
          boxShadow: "0 10px 20px rgba(0, 0, 0, 0.15)",
        }}
      >
        <h1
          style={{
            fontWeight: "bold",
            fontSize: "2.5rem",
            textAlign: "center",
            marginBottom: "30px",
          }}
        >
          จัดการห้องเรียน
        </h1>

        {classInfo && (
          <div
            style={{
              padding: "20px",
              marginBottom: "30px",
              boxShadow: "0 4px 8px rgba(0, 0, 0, 0.3)",
              borderRadius: "10px",
              backgroundColor: "#ffffff",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              border: "2.25px solid #9579cd",
            }}
          >
            <div style={{ textAlign: "center" }}>
              <h4>{classInfo.info.name} ({classInfo.info.code})</h4>
              <QRCodeCanvas value={`${window.location.origin}/register/${classId}`} size={180} />
            </div>
          </div>
        )}

        <button
          onClick={() => navigate(`/checkin/${classId}`)}
          style={{
            backgroundColor: "#000000",
            color: "#ffffff",
            padding: "16px",
            border: "none",
            borderRadius: "10px",
            cursor: "pointer",
            fontSize: "16px",
            width: "100%",
            fontWeight: "bold",
            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
            transition: "background-color 0.3s ease-in-out, color 0.3s ease-in-out",
            marginBottom: "20px",
          }}
          onMouseEnter={(e) => {
            e.target.style.backgroundColor = "#cdb6fb";
            e.target.style.color = "#000000";
          }}
          onMouseLeave={(e) => {
            e.target.style.backgroundColor = "#000000";
            e.target.style.color = "#ffffff";
          }}
        >
          เริ่มเช็คชื่อ
        </button>

        <h5 className="mb-3">ประวัติการเช็คชื่อ</h5>
        <div className="table-responsive">
          <table className="table table-bordered text-center">
            <thead className="table-dark">
              <tr>
                <th>#</th>
                <th>วัน-เวลา</th>
                <th>จำนวนคนเข้าเรียน</th>
                <th>สถานะ</th>
              </tr>
            </thead>
            <tbody>
              {checkins.map((record, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{record.date}</td>
                  <td>{record.studentCount}</td>
                  <td>{record.status === 1 ? "กำลังเรียน" : "เสร็จสิ้น"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <button
          onClick={() => navigate("/dashboard")}
          style={{
            backgroundColor: "#cdb6fb",
            color: "white",
            padding: "14px",
            border: "none",
            borderRadius: "10px",
            cursor: "pointer",
            fontSize: "16px",
            fontWeight: "bold",
            width: "100%",
            marginTop: "20px",
            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
            transition: "background-color 0.3s ease-in-out, color 0.3s ease-in-out",
          }}
          onMouseEnter={(e) => {
            e.target.style.backgroundColor = "#9579cd";
          }}
          onMouseLeave={(e) => {
            e.target.style.backgroundColor = "#cdb6fb";
          }}
        >
          กลับไป Dashboard
        </button>
      </div>
    </div>
  );
};

export default ManageClass;
