import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { db } from "../firebase";
import { doc, getDoc, collection, query, getDocs, where } from "firebase/firestore"; // เพิ่ม where
import { QRCodeCanvas } from "qrcode.react";
import "bootstrap/dist/css/bootstrap.min.css";

const ManageClass = () => {
  const { classId } = useParams();
  const navigate = useNavigate();
  const [classInfo, setClassInfo] = useState(null);
  const [checkins, setCheckins] = useState([]);
  const [students, setStudents] = useState([]); // เก็บรายชื่อนักศึกษา "ทั้งหมด" ในห้อง
  const [showStudents, setShowStudents] = useState(false);
  const [selectedCheckinStudents, setSelectedCheckinStudents] = useState(null);

  useEffect(() => {
    const fetchClassData = async () => {
      if (!classId) return;
      const classRef = doc(db, "classroom", classId);
      const classSnap = await getDoc(classRef);
      if (classSnap.exists()) {
        setClassInfo(classSnap.data());
      } else {
        console.error("Class not found");
        navigate("/dashboard");
      }
    };

    const fetchCheckins = async () => {
      const checkinQuery = query(collection(db, `classroom/${classId}/checkin`));
      const checkinSnapshot = await getDocs(checkinQuery);

      const checkinData = await Promise.all(
        checkinSnapshot.docs.map(async (docSnap) => {
          const checkinInfo = docSnap.data();
          const studentQuery = collection(
            db,
            `classroom/${classId}/checkin/${docSnap.id}/students`
          );
          const studentSnapshot = await getDocs(studentQuery);

          return {
            id: docSnap.id,
            ...checkinInfo,
            studentCount: studentSnapshot.size,
          };
        })
      );

      setCheckins(checkinData);
    };

    // ดึงข้อมูลนักศึกษา "ทั้งหมด" ในห้องเรียน (ครั้งเดียว)
    const fetchAllStudents = async () => {
      const studentQuery = query(collection(db, `classroom/${classId}/students`));
      const studentSnapshot = await getDocs(studentQuery);
      const studentList = studentSnapshot.docs.map((docSnap) => ({
        id: docSnap.id,  // เก็บ id (uid) ของนักศึกษา
        ...docSnap.data(), // เก็บ stdid, name, photo
      }));
      setStudents(studentList);
    };


    fetchClassData();
    fetchCheckins();
    fetchAllStudents(); // เรียกฟังก์ชันเพื่อดึงข้อมูลนักศึกษาทั้งหมด
    setSelectedCheckinStudents(null);
  }, [classId, navigate]);

  const handleStartCheckIn = () => {
    navigate(`/checkin/${classId}`);
  };



const handleShowStudents = async (checkinId) => {
  if (!checkinId) {
    setSelectedCheckinStudents(null);
    return;
  }

  try {
    const studentsQuery = query(collection(db, `classroom/${classId}/checkin/${checkinId}/students`));
    const studentsSnapshot = await getDocs(studentsQuery);

    // สร้าง array ชั่วคราวเพื่อเก็บข้อมูลที่รวมชื่อและรหัสแล้ว
    const enrichedStudentsData = [];

    for (const checkinDoc of studentsSnapshot.docs) {
      const checkinData = checkinDoc.data(); // ข้อมูลการเช็คชื่อ (มีแค่ id)

      // ค้นหานักศึกษาใน "students" array (ที่เราดึงมาทั้งหมด) โดยใช้ id
      const student = students.find((s) => s.id === checkinDoc.id); // ใช้ checkinDoc.id ที่เป็น uid

      if (student) {
        // ถ้านักศึกษาคนนั้นอยู่ในรายชื่อนักศึกษาทั้งหมด
        enrichedStudentsData.push({
          stdid: student.stdid, // เอารหัสจาก student (ที่มาจาก students)
          name: student.name,   // เอาชื่อจาก student (ที่มาจาก students)
          photo: student.photo, //เอารูปจาก student
        });
      } else {
          console.warn(`Student with ID ${checkinDoc.id} not found in the main student list.`);
          // Handle the case where a student in check-in data is not found in the main student list.
          // You might want to add some default data or skip this student.
          enrichedStudentsData.push({ //ใส่ข้อมูล default
            stdid: "N/A",
            name: "Unknown Student",
            photo: "", // Or a default image URL
          });
        }

    }

    setSelectedCheckinStudents(enrichedStudentsData); // เก็บข้อมูลที่รวมเข้า state
  } catch (error) {
    console.error("Error fetching check-in students:", error);
  }
};



  return (
    <div className="container mt-5">
      <h1 className="mb-4 text-center">จัดการห้องเรียน</h1>
      {classInfo && (
        <div className="card mb-4">
          <div className="card-body text-center">
            <h4>{classInfo.info.name} ({classInfo.info.code})</h4>
            <QRCodeCanvas value={`${window.location.origin}/register/${classId}`} size={180} />
          </div>
        </div>
      )}

      <button className="btn btn-primary mb-3" onClick={handleStartCheckIn}>
        เริ่มเช็คชื่อ
      </button>

      <h5>ประวัติการเช็คชื่อ</h5>
      <table className="table">
        <thead>
          <tr>
            <th>#</th>
            <th>วัน-เวลา</th>
            <th>จำนวนคนเข้าเรียน</th>
            <th>สถานะ</th>
            <th>รายชื่อ</th>
          </tr>
        </thead>
        <tbody>
          {checkins.map((record, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{record.date}</td>
              <td>{record.studentCount}</td>
              <td>{record.status === 1 ? "กำลังเรียน" : "เสร็จสิ้น"}</td>
              <td>
                <button
                  className="btn btn-sm btn-info"
                  onClick={() => handleShowStudents(record.id)}
                >
                  รายชื่อ
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedCheckinStudents && (
        <div className="mt-3">
          <h6>รายชื่อนักเรียนที่เข้าเรียน (ครั้งที่เลือก)</h6>
          <table className="table table-sm">
            <thead>
              <tr>
                <th>รหัส</th>
                <th>ชื่อ</th>
                {/*<th>รูป</th>*/}
              </tr>
            </thead>
            <tbody>
              {selectedCheckinStudents.map((student, index) => (
                <tr key={index}>
                  <td>{student.stdid}</td>
                  <td>{student.name}</td>
                  {/*<td><img src={student.photo} alt={student.name} width="30" height="30"/></td>*/}
                </tr>
              ))}
            </tbody>
          </table>
          <button
            className="btn btn-sm btn-secondary"
            onClick={() => setSelectedCheckinStudents(null)}
          >
            ซ่อนรายชื่อ
          </button>
        </div>
      )}

      <button
        className="btn btn-secondary mt-3"
        onClick={() => navigate("/dashboard")}
      >
        กลับไป Dashboard
      </button>

      <button
        className="btn btn-info mt-3 ms-2"
        onClick={() => setShowStudents(!showStudents)}
      >
        {showStudents ? "ซ่อนรายชื่อนักศึกษา" : "แสดงรายชื่อนักศึกษา"}
      </button>

      {showStudents && (
        <div className="mt-4">
          <h5>รายชื่อนักศึกษา</h5>
          <table className="table">
            <thead>
              <tr>
                <th>#</th>
                <th>รหัส</th>
                <th>ชื่อ</th>
                <th>รูปภาพ</th>
              </tr>
            </thead>
            <tbody>
              {students.map((student, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{student.stdid}</td>
                  <td>{student.name}</td>
                  <td>
                    <img src={student.photo} alt="Student" width="50" height="50" />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ManageClass;
