import { useState, useEffect, useCallback } from "react";
import { db } from "../firebase";
import { getAuth, signOut } from "firebase/auth";
import { collection, query, where, getDocs, doc, getDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css"; // Keep Bootstrap if you want the basic button styles, etc.


const Dashboard = () => {
  const [classes, setClasses] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const fetchClasses = useCallback(async () => {
    const auth = getAuth();
    if (!auth.currentUser) return;

    const q = query(collection(db, "classroom"), where("owner", "==", auth.currentUser.uid));
    const querySnapshot = await getDocs(q);
    setClasses(querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      const auth = getAuth();
      if (!auth.currentUser) {
        navigate("/");
        return;
      }

      const userDocRef = doc(db, "users", auth.currentUser.uid);
      const docSnap = await getDoc(userDocRef);

      if (docSnap.exists()) {
        setUser(docSnap.data());
      } else {
        console.error("User not found in Firestore");
        navigate("/");
      }
    };

    fetchData();
    fetchClasses();
  }, [fetchClasses, navigate]);


  const handleLogout = async () => {
    const auth = getAuth();
    try {
      await signOut(auth);
      navigate("/");
    } catch (error) {
      console.error("Logout failed:", error);
    }
  };



  return (
    <div className="bg-gray-100 min-h-screen font-sans">
      <header className="bg-indigo-600 text-white p-4">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold">
            <span role="img" aria-label="books">📚</span> Dashboard
          </h1>
          <button onClick={handleLogout} className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition duration-150 ease-in-out">
            ออกจากระบบ
          </button>
        </div>
      </header>

      <main className="container mx-auto mt-6">
        <div className="bg-white rounded-lg shadow-md p-6">

          {/* User Profile Section */}
          {user && (
            <div className="flex items-center mb-6 pb-4 border-b border-gray-200">
              <img
                src={user.photo || "default-profile-pic.jpg"} // Consider a default profile picture
                alt="User Profile"
                className="w-16 h-16 rounded-full object-cover mr-4"
              />
              <div>
                <h2 className="text-xl font-semibold">{user.name}</h2>
                <p className="text-gray-600">{user.email}</p>
              </div>
            </div>
          )}

            {/* Action Buttons */}
          <div className="flex gap-4 mb-6">
            <button
              onClick={() => navigate("/edit-profile")}
              className="flex items-center justify-center px-4 py-2 border border-blue-500 text-blue-500 hover:bg-blue-500 hover:text-white rounded-md transition duration-150 ease-in-out"
            >
              <span role="img" aria-label="edit">✏️</span>
              <span className="ml-2">แก้ไขโปรไฟล์</span>
            </button>
            <button
              onClick={() => navigate("/add-class")}
              className="flex items-center justify-center px-4 py-2 border border-green-500 text-green-500 hover:bg-green-500 hover:text-white rounded-md transition duration-150 ease-in-out"
            >
             <span role="img" aria-label="add">➕</span>
              <span className="ml-2">เพิ่มห้องเรียน</span>
            </button>
          </div>


          {/* Classrooms Section */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {classes.length > 0 ? (
              classes.map((cls) => (
                <div
                  key={cls.id}
                  className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition duration-200 ease-in-out"
                >
                  {cls.info.photo && (
                    <img
                      src={cls.info.photo}
                      alt="Class"
                      className="w-full h-40 object-cover"
                    />
                  )}
                  <div className="p-4">
                    <h3 className="text-lg font-semibold text-gray-800 mb-1">
                      {cls.info.code} - {cls.info.name}
                    </h3>
                    <p className="text-gray-600 mb-3">
                      <span role="img" aria-label="location">📍</span> ห้องเรียน: {cls.info.room}
                    </p>
                    <button
                      onClick={() => navigate(`/manage-class/${cls.id}`)}
                      className="w-full bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition duration-150 ease-in-out"
                    >
                      จัดการห้องเรียน
                    </button>
                  </div>
                </div>
              ))
            ) : (
              <p className="text-gray-600 col-span-full">
                <span role="img" aria-label="pin">📌</span> ยังไม่มีห้องเรียน
              </p>
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
