import { useState, useEffect, useCallback } from "react";
import { db } from "../firebase";
import { getAuth, signOut } from "firebase/auth";
import { collection, query, where, getDocs, doc, getDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

// ✅ คอมโพเนนต์ปุ่มที่เปลี่ยน Gradient ตามเมาส์เข้า
const GradientButton = ({ text, onClick }) => {
  const [gradientDirection, setGradientDirection] = useState("to right");

  const handleMouseEnter = (e) => {
    const { offsetX, offsetY, target } = e.nativeEvent;
    const { clientWidth, clientHeight } = target;

    const fromLeft = offsetX < clientWidth / 3;
    const fromRight = offsetX > (clientWidth * 2) / 3;
    const fromTop = offsetY < clientHeight / 3;
    const fromBottom = offsetY > (clientHeight * 2) / 3;

    if (fromLeft) setGradientDirection("to right");
    else if (fromRight) setGradientDirection("to left");
    else if (fromTop) setGradientDirection("to bottom");
    else if (fromBottom) setGradientDirection("to top");
  };

  return (
    <button
      onClick={onClick}
      onMouseEnter={handleMouseEnter}
      style={{
        background: `linear-gradient(${gradientDirection}, #cdb6fb 0%, #000000 100%)`,
        backgroundSize: "200% 100%",
        backgroundPosition: "right bottom",
        color: "#ffffff",
        padding: "16px 20px",
        borderRadius: "8px",
        cursor: "pointer",
        fontSize: "16px",
        border: "2px solid black",
        transition: "background-position 0.3s ease-in-out, color 0.3s ease-in-out",
      }}
    >
      {text}
    </button>
  );
};

const Dashboard = () => {
  const [classes, setClasses] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const fetchClasses = useCallback(async () => {
    const auth = getAuth();
    if (!auth.currentUser) return;

    const q = query(collection(db, "classroom"), where("owner", "==", auth.currentUser.uid));
    const querySnapshot = await getDocs(q);
    setClasses(querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      const auth = getAuth();
      if (!auth.currentUser) {
        navigate("/");
        return;
      }

      const userDocRef = doc(db, "users", auth.currentUser.uid);
      const docSnap = await getDoc(userDocRef);

      if (docSnap.exists()) {
        setUser(docSnap.data());
      } else {
        console.error("User not found in Firestore");
        navigate("/");
      }
    };

    fetchData();
    fetchClasses();
  }, [fetchClasses, navigate]);

  const handleLogout = async () => {
    const auth = getAuth();
    try {
      await signOut(auth);
      navigate("/");
    } catch (error) {
      console.error("Logout failed:", error);
    }
  };

  return (
    <div style={{ background: "#f6f4fd", minHeight: "100vh", padding: "30px 0", fontFamily: "'Arial', sans-serif" }}>
      <div
        style={{
          maxWidth: "1100px",
          margin: "0 auto",
          background: "#ffffff",
          padding: "40px",
          borderRadius: "15px",
          boxShadow: "0 10px 20px rgba(0, 0, 0, 0.15)",
        }}
      >
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "30px" }}>
          <h1
            style={{
              fontWeight: "bold",
              fontSize: "6rem",
              position: "relative",
              display: "inline-block",
              background: "linear-gradient(90deg, rgba(149, 121, 205, 1) 0%, rgba(0, 0, 0, 1) 100%)",
              WebkitBackgroundClip: "text",
              color: "transparent",
            }}
          >
            Dashboard
          </h1>
          <button
            onClick={handleLogout}
            style={{
              backgroundColor: "#000000",
              color: "white",
              border: "none",
              padding: "12px 18px",
              borderRadius: "10px",
              cursor: "pointer",
              fontSize: "16px",
            }}
          >
            ออกจากระบบ
          </button>
        </div>

        {user && (
          <div style={{ padding: "20px", marginBottom: "30px", borderRadius: "10px", backgroundColor: "#ffffff", display: "flex", border: "2.25px solid #9579cd", alignItems: "center" }}>
            <img src={user.photo || "default-profile-pic.jpg"} alt="User Profile" style={{ width: "75px", height: "75px", objectFit: "cover", borderRadius: "50%", marginRight: "20px" }} />
            <div>
              <h4 style={{ marginBottom: "10px", fontSize: "20px" }}>{user.name}</h4>
              <p style={{ color: "#666", marginBottom: "0" }}>{user.email}</p>
            </div>
          </div>
        )}

        {/* ✅ ปุ่มแก้ไขโปรไฟล์ & เพิ่มห้องเรียน */}
        <div style={{ marginBottom: "30px", display: "flex", gap: "20px" }}>
          <GradientButton text="แก้ไขโปรไฟล์" onClick={() => navigate("/edit-profile")} />
          <GradientButton text="เพิ่มห้องเรียน" onClick={() => navigate("/add-class")} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
