import { auth, provider, db } from "../firebase";
import { signInWithPopup } from "firebase/auth";
import { doc, setDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";


const Login = () => {
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      await setDoc(doc(db, "users", user.uid), {
        name: user.displayName,
        email: user.email,
        photo: user.photoURL,
      }, { merge: true });

      navigate("/dashboard");
    } catch (error) {
      console.error("Login Failed", error);
      // ✅ เพิ่ม: แสดงข้อความ error ให้ผู้ใช้ (optional)
      alert("Login failed! Please try again.");
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="card p-4 shadow-lg text-center" style={{ maxWidth: "400px", width: "90%" }}> {/* ✅ ปรับขนาด card */}
        <h1 className="mb-4">เข้าสู่ระบบ</h1>
        <button onClick={handleLogin} className="btn btn-primary btn-lg"> {/* ✅ เพิ่มขนาดปุ่ม */}
          <i className="bi bi-google me-2"></i> {/* ✅ ใช้ Bootstrap Icon และเพิ่มระยะห่าง */}
          Login with Google
        </button>
        {/* ✅ เพิ่ม: ส่วนของข้อความต้อนรับ (optional) */}
        <p className="mt-3 text-muted">
          Sign in to access your dashboard and manage your account.
        </p>
      </div>
    </div>
  );
};

export default Login;
