import { useState, useEffect, useCallback } from "react";
import { db } from "../firebase";
import { getAuth, signOut } from "firebase/auth";
import { collection, query, where, getDocs, doc, getDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const Dashboard = () => {
  const [classes, setClasses] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const fetchClasses = useCallback(async () => {
    const auth = getAuth();
    if (!auth.currentUser) return;

    const q = query(collection(db, "classroom"), where("owner", "==", auth.currentUser.uid));
    const querySnapshot = await getDocs(q);
    setClasses(querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() })));
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      const auth = getAuth();
      if (!auth.currentUser) {
        navigate("/");
        return;
      }

      const userDocRef = doc(db, "users", auth.currentUser.uid);
      const docSnap = await getDoc(userDocRef);

      if (docSnap.exists()) {
        setUser(docSnap.data());
      } else {
        console.error("User not found in Firestore");
        navigate("/");
      }
    };

    fetchData();
    fetchClasses();
  }, [fetchClasses, navigate]);

  const handleLogout = async () => {
    const auth = getAuth();
    try {
      await signOut(auth);
      navigate("/");
    } catch (error) {
      console.error("Logout failed:", error);
    }
  };

  return (
    <div
      style={{
        background: "#F4F3EE",
        minHeight: "100vh",
        padding: "30px 0",
        fontFamily: "'Arial', sans-serif",
      }}
    >
      <div
        style={{
          maxWidth: "1100px",
          margin: "0 auto",
          backgroundColor: "#FFFFFF",
          padding: "40px",
          borderRadius: "15px",
          boxShadow: "0 10px 20px rgba(0, 0, 0, 0.1)",
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: "30px",
          }}
        >
          <h1 style={{ fontWeight: "bold", color: "#3E3D53" }}>Dashboard</h1>
          <button
            onClick={handleLogout}
            style={{
              backgroundColor: "#3E3D53",
              color: "white",
              border: "none",
              padding: "12px 18px",
              borderRadius: "10px",
              cursor: "pointer",
              fontSize: "16px",
            }}
          >
            ออกจากระบบ
          </button>
        </div>

        {user && (
          <div
            style={{
              padding: "20px",
              marginBottom: "30px",
              borderRadius: "10px",
              backgroundColor: "#E8E6E1",
              display: "flex",
              alignItems: "center",
            }}
          >
            <img
              src={user.photo || "default-profile-pic.jpg"}
              alt="User Profile"
              style={{
                width: "75px",
                height: "75px",
                objectFit: "cover",
                borderRadius: "50%",
                marginRight: "20px",
              }}
            />
            <div>
              <h4 style={{ marginBottom: "10px", fontSize: "20px" }}>{user.name}</h4>
              <p style={{ color: "#666", marginBottom: "0" }}>{user.email}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
