import { useState, useEffect } from "react";
import { getAuth, updateProfile } from "firebase/auth";
import { db } from "../firebase";
import { doc, updateDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";
//import imageCompression from 'browser-image-compression'; // Import library
import Resizer from 'react-image-file-resizer';

const EditProfile = () => {
  const auth = getAuth();
  const [userInfo, setUserInfo] = useState({
    name: "",
    email: "",
    photoURL: "",
  });
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [uploading, setUploading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (auth.currentUser) {
      setUserInfo({
        name: auth.currentUser.displayName || "",
        email: auth.currentUser.email || "",
        photoURL: auth.currentUser.photoURL || "",
      });
    } else {
      navigate("/"); // Redirect if not logged in
    }
  }, [auth, navigate]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
        const MAX_FILE_SIZE_MB = 2; // กำหนดขนาดสูงสุดที่ยอมรับได้ (MB)
        if (file.size > MAX_FILE_SIZE_MB * 1024 * 1024) {
          alert(`File size must be less than ${MAX_FILE_SIZE_MB}MB`);
          return; // หยุดการทำงานถ้าไฟล์ใหญ่เกิน
        }
        Resizer.imageFileResizer(
            file,
            500, // maxWidth
            500, // maxHeight
            'JPEG', // compressFormat
            70, // quality (0-100)
            0, // rotation
            (uri) => { // callback function
                setImageFile(uri);
                setImagePreview(uri);

            },
            'base64' // outputType ('base64', 'blob', 'file')
        );
    }
};

  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    setUploading(true);
    try {
      let imageUrl = userInfo.photoURL;

      if (imageFile) {
        // No need to re-read as Data URL if using imageCompression
        // We already have the compressed image in imageFile
//       imageUrl = await new Promise((resolve, reject) => {
//           const reader = new FileReader();
//           reader.onloadend = () => {
//               resolve(reader.result)
//           }
//           reader.onerror = reject;
//           reader.readAsDataURL(imageFile);

//       });
            imageUrl = imageFile;
      }

      await updateProfile(auth.currentUser, {
        displayName: userInfo.name,  // Include displayName
        photoURL: imageUrl,
      });

      const userRef = doc(db, "users", auth.currentUser.uid);
      await updateDoc(userRef, {
        name: userInfo.name,
        email: userInfo.email,
        photo: imageUrl,
      });

      alert("อัปเดตโปรไฟล์สำเร็จ!");
      navigate("/dashboard");
    } catch (error) {
      console.error("Error updating profile: ", error);
      alert("เกิดข้อผิดพลาดในการอัปเดตโปรไฟล์!");
    } finally {
      setUploading(false);
    }
  };
  return (
    <div className="container mt-5">
      <h1 className="mb-3">แก้ไขข้อมูลส่วนตัว</h1>
      <form onSubmit={handleUpdateProfile}>
        <div className="mb-3 text-center">
          {imagePreview ? (
            <img
              src={imagePreview}
              alt="Profile Preview"
              className="img-thumbnail w-25 mb-2"
            />
          ) : (
            <img
              src={userInfo.photoURL || "default-profile-pic.jpg"}
              alt="Current Profile"
              className="img-thumbnail w-25 mb-2"
            />
          )}
        </div>

        <div className="mb-3">
          <label className="form-label">อัปโหลดรูปภาพใหม่</label>
          <input type="file" className="form-control" onChange={handleImageChange} />
        </div>

        <div className="mb-3">
          <label className="form-label">ชื่อ</label>
          <input
            type="text"
            className="form-control"
            value={userInfo.name}
            onChange={(e) => setUserInfo({ ...userInfo, name: e.target.value })}
          />
        </div>

        <div className="mb-3">
          <label className="form-label">อีเมล</label>
          <input type="email" className="form-control" value={userInfo.email} disabled />
        </div>

        <button type="submit" className="btn btn-success" disabled={uploading}>
          {uploading ? "กำลังอัปโหลด..." : "อัปเดตข้อมูล"}
        </button>
      </form>
    </div>
  );
};

export default EditProfile;
