        <div className="grid grid-cols-2 gap-6">
          {classes.length > 0 ? (
            classes.map((cls) => (
              <Card key={cls.id} className="cursor-pointer hover:shadow-lg">
                {cls.info.photo && <img src={cls.info.photo} alt="Class" className="w-full h-40 object-cover rounded-t-xl" />}
                <CardContent className="p-4">
                  <h5 className="text-lg font-semibold">{cls.info.code} - {cls.info.name}</h5>
                  <p className="text-gray-500">üìç ‡∏´‡πâ‡∏≠‡∏á: {cls.info.room}</p>
