import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { db } from "../firebase";
import { doc, getDoc, collection, query, getDocs } from "firebase/firestore";
import { QRCodeCanvas } from "qrcode.react";
import "bootstrap/dist/css/bootstrap.min.css";

const ManageClass = () => {
  const { classId } = useParams();
  const navigate = useNavigate();
  const [classInfo, setClassInfo] = useState(null);
  const [checkins, setCheckins] = useState([]);
  const [students, setStudents] = useState([]); // เก็บรายชื่อ "ทั้งหมด"
  const [showStudents, setShowStudents] = useState(false);
  const [selectedCheckin, setSelectedCheckin] = useState(null); // ข้อมูล checkin ครั้งที่เลือก
  const [selectedCheckinStudents, setSelectedCheckinStudents] = useState(null); // นักเรียนที่เช็คชื่อในครั้งนั้น
  const [selectedCheckinAnswers, setSelectedCheckinAnswers] = useState(null); // คำตอบ

  useEffect(() => {
    // ... (fetchClassData, fetchCheckins, fetchAllStudents เหมือนเดิม) ...
    const fetchClassData = async () => {
      if (!classId) return;
      const classRef = doc(db, "classroom", classId);
      const classSnap = await getDoc(classRef);
      if (classSnap.exists()) {
        setClassInfo(classSnap.data());
      } else {
        console.error("Class not found");
        navigate("/dashboard");
      }
    };

    const fetchCheckins = async () => {
      const checkinQuery = query(collection(db, `classroom/${classId}/checkin`));
      const checkinSnapshot = await getDocs(checkinQuery);

      const checkinData = await Promise.all(
        checkinSnapshot.docs.map(async (docSnap) => {
          const checkinInfo = docSnap.data();
          return {
            id: docSnap.id,
            ...checkinInfo,
          };
        })
      );
      setCheckins(checkinData);
    };

    const fetchAllStudents = async () => {
      const studentQuery = query(collection(db, `classroom/${classId}/students`));
      const studentSnapshot = await getDocs(studentQuery);
      const studentList = studentSnapshot.docs.map((docSnap) => ({
        id: docSnap.id, // id คือ UID
        ...docSnap.data(), // stdid, name, photo
      }));
      setStudents(studentList);
    };


    fetchClassData();
    fetchCheckins();
    fetchAllStudents();

    setSelectedCheckin(null);
    setSelectedCheckinStudents(null);
    setSelectedCheckinAnswers(null);
  }, [classId, navigate]);

  const handleStartCheckIn = () => {
    navigate(`/checkin/${classId}`);
  };



  const handleShowStudents = async (checkinId) => {
    if (!checkinId) {
      setSelectedCheckin(null);
      setSelectedCheckinStudents(null);
      setSelectedCheckinAnswers(null);
      return;
    }

    try {
      // 1. ดึงข้อมูล check-in (เพื่อวันที่, question_text, ฯลฯ)
      const checkinRef = doc(db, `classroom/${classId}/checkin`, checkinId);
      const checkinSnap = await getDoc(checkinRef);
      if (!checkinSnap.exists()) {
        console.error("Check-in not found");
        return;
      }
      const checkinData = checkinSnap.data();
      //console.log("Check-in data:", checkinData); // Debug: ดูข้อมูล check-in
      setSelectedCheckin({ id: checkinId, ...checkinData });



      // 2. ดึงข้อมูลนักเรียน (เหมือนเดิม)
      const studentsQuery = query(collection(db, `classroom/${classId}/checkin/${checkinId}/students`));
      const studentsSnapshot = await getDocs(studentsQuery);
      const studentsData = [];

      for (const checkinDoc of studentsSnapshot.docs) {
          const checkinData = checkinDoc.data();
           const student = students.find((s) => s.id === checkinDoc.id);
           if(student){
               studentsData.push({
                stdid: student.stdid,
                name: student.name
               })
           } else {
             console.warn(`Student with ID ${checkinDoc.id} not found in students array.`);
              studentsData.push({ //ข้อมูล default
                stdid: "N/A",
                name: "Unknown Student",
                photo: "",
              });
           }
      }
       setSelectedCheckinStudents(studentsData);


      // 3. ดึงข้อมูล *คำตอบ* (ปรับปรุง)
      const answersQuery = query(collection(db, `classroom/${classId}/checkin/${checkinId}/answers`));
      const answersSnapshot = await getDocs(answersQuery);
      const answersData = {}; // { "qno": { students: { "uid": { text: "...", time: "...", stdid: "...", name: "..." }, ... } }, ... }

      for (const answerDoc of answersSnapshot.docs) {
        const qno = answerDoc.id; // ID ของ *ชุด* คำถาม/คำตอบ
        const answerData = answerDoc.data(); // ตอนนี้จะเหลือแค่ {}  (ไม่มี text)

        answersData[qno] = {  students: {} }; // *ไม่* มี text  แล้ว

        // ดึงคำตอบของนักเรียน
        const studentAnswersQuery = query(collection(db, `classroom/${classId}/checkin/${checkinId}/answers/${qno}/students`));
        const studentAnswersSnapshot = await getDocs(studentAnswersQuery);

        for (const studentAnswerDoc of studentAnswersSnapshot.docs) {
          const studentId = studentAnswerDoc.id; // UID
          const studentAnswerData = studentAnswerDoc.data(); // { text: "...", time: "..." }

          const student = students.find((s) => s.id === studentId);
          if (student) {
            answersData[qno].students[studentId] = {
              ...studentAnswerData, // text, time
              stdid: student.stdid,
              name: student.name,
            };
          }  else {
              console.warn(`Student with ID ${studentId} not found for answer ${qno}.`);
              answersData[qno].students[studentId] = { //ข้อมูล default
                ...studentAnswerData,
                stdid: "N/A",
                name: "Unknown",
            };
          }

        }
      }

      //console.log("Answers data:", answersData); // Debug: ดูโครงสร้างข้อมูลคำตอบ
      setSelectedCheckinAnswers(answersData);

    } catch (error) {
      console.error("Error fetching check-in data:", error);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4 text-center">จัดการห้องเรียน</h1>
      {/* ... (ส่วนแสดงข้อมูลห้องเรียน, QR Code, ปุ่มเริ่มเช็คชื่อ เหมือนเดิม) ... */}
       {classInfo && (
        <div className="card mb-4">
          <div className="card-body text-center">
            <h4>{classInfo.info.name} ({classInfo.info.code})</h4>
            <QRCodeCanvas value={`${window.location.origin}/register/${classId}`} size={180} />
          </div>
        </div>
      )}

      <button className="btn btn-primary mb-3" onClick={handleStartCheckIn}>
        เริ่มเช็คชื่อ
      </button>

      <h5>ประวัติการเช็คชื่อ</h5>
      <table className="table">
        <thead>
          <tr>
            <th>#</th>
            <th>วัน-เวลา</th>
            <th>จำนวนคนเข้าเรียน</th>
            <th>สถานะ</th>
            <th>รายชื่อ</th>
          </tr>
        </thead>
        <tbody>
          {checkins.map((record, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{record.date}</td>
              <td>{record.studentCount}</td>
              <td>{record.status === 1 ? "กำลังเรียน" : "เสร็จสิ้น"}</td>
              <td>
                <button
                  className="btn btn-sm btn-info"
                  onClick={() => handleShowStudents(record.id)}
                >
                  รายชื่อ
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>


      {/* แสดงรายชื่อ + คำตอบ */}
      {selectedCheckin && selectedCheckinStudents && (
        <div className="mt-3">
          <h6>
            รายชื่อนักเรียนที่เข้าเรียน (วันที่: {selectedCheckin.date})
          </h6>
          <table className="table table-sm">
            <thead>
              <tr>
                <th>รหัส</th>
                <th>ชื่อ</th>
              </tr>
            </thead>
            <tbody>
              {selectedCheckinStudents.map((student) => (
                <tr key={student.stdid}>
                  <td>{student.stdid}</td>
                  <td>{student.name}</td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* แสดงคำตอบ */}
        {selectedCheckinAnswers && Object.keys(selectedCheckinAnswers).length > 0 && (
            <div className="mt-4">
              <h5>คำตอบ:</h5>
              {Object.entries(selectedCheckinAnswers).map(([qno, answerData]) => {
                // ใช้ selectedCheckin.question_text  เพื่อหาข้อความคำถาม
                const questionText = selectedCheckin.question_text || `คำถามข้อ ${qno}`;

                return (
                <div key={qno} className="mb-3">
                  {/* แสดงคำถาม */}
                  <h6>{questionText}</h6>
                  <table className="table table-bordered table-sm">
                    <thead>
                      <tr>
                        <th>รหัส</th>
                        <th>ชื่อ</th>
                        <th>คำตอบ</th>
                        <th>เวลา</th>
                      </tr>
                    </thead>
                    <tbody>
                      {/* แสดงคำตอบของนักเรียน */}
                      {Object.entries(answerData.students).map(([studentId, studentAnswer]) => (
                        <tr key={studentId}>
                          <td>{studentAnswer.stdid}</td>
                          <td>{studentAnswer.name}</td>
                          <td>{studentAnswer.text}</td>
                          <td>{studentAnswer.time}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              );
            })}
            </div>
          )}

          <button
            className="btn btn-sm btn-secondary"
            onClick={() => {
              setSelectedCheckin(null);
              setSelectedCheckinStudents(null);
              setSelectedCheckinAnswers(null);
            }}
          >
            ซ่อนรายละเอียด
          </button>
        </div>
      )}

      {/* ... (ส่วนอื่นๆ เหมือนเดิม: กลับไป Dashboard, แสดง/ซ่อนรายชื่อนักศึกษาทั้งหมด) ... */}

       <button
        className="btn btn-secondary mt-3"
        onClick={() => navigate("/dashboard")}
      >
        กลับไป Dashboard
      </button>

      <button
        className="btn btn-info mt-3 ms-2"
        onClick={() => setShowStudents(!showStudents)}
      >
        {showStudents ? "ซ่อนรายชื่อนักศึกษา" : "แสดงรายชื่อนักศึกษา"}
      </button>

      {showStudents && (
        <div className="mt-4">
          <h5>รายชื่อนักศึกษา</h5>
          <table className="table">
            <thead>
              <tr>
                <th>#</th>
                <th>รหัส</th>
                <th>ชื่อ</th>
                <th>รูปภาพ</th>
              </tr>
            </thead>
            <tbody>
              {students.map((student, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{student.stdid}</td>
                  <td>{student.name}</td>
                  <td>
                    <img src={student.photo} alt="Student" width="50" height="50" />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ManageClass;
