import { auth, provider, db } from "../firebase";
import { signInWithPopup } from "firebase/auth";
import { doc, setDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import { FcGoogle } from "react-icons/fc"; // Import a Google icon

const Login = () => {
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      await setDoc(
        doc(db, "users", user.uid),
        {
          name: user.displayName,
          email: user.email,
          photo: user.photoURL,
        },
        { merge: true }
      );

      navigate("/dashboard");
    } catch (error) {
      console.error("Login Failed", error);
    }
  };

  return (
    <div
      style={{
        background: "linear-gradient(120deg, #ffe6e6 0%, #ffb3b3 100%)", // Light gradient background
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        fontFamily: "'Arial', sans-serif", // Set a default font
      }}
    >
      <div
        style={{
          backgroundColor: "#ffffff", // White card background
          padding: "40px",
          borderRadius: "15px",
          boxShadow: "0 10px 20px rgba(0, 0, 0, 0.15)",
          textAlign: "center",
          maxWidth: "400px", // Limit card width
          width: "90%", // Make card responsive
        }}
      >
        <h1 style={{ fontWeight: "bold", color: "#e74c3c", marginBottom: "30px" }}>
